import React, { useState, FunctionComponent } from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
    flex: 1;
`;

const ContentRow = styled.div`
    flex: 1;
    flex-direction: row;
`;

const Header = styled.div`
    flex: 1;
    flex-direction: row;
`;

const HeaderTitle = styled.p`
    flex: 6;
    font-size: 15px;
    line-height: 18px;
`;

const IconWrapper = styled.div`
    flex: 1;
    align-items: flex-end;
`;

const CustomIconWrapper = styled.div`
    flex: 1;
    align-items: flex-end;
    padding: 12px;
`;

const Content = styled.p`
    flex: 6;
    margin-top: 12px;
    font-size: 13px;
    line-height: 18px;
    color: ${({ theme }) => theme.colors.contrast['500']};
`;

const BodyWrapper = styled.div`
    flex: 1;
`;

const CustomHeaderTitle = styled.p`
    font-size: ${({ theme }) => theme.fonts.size[14]};
    color: ${({ theme }) => theme.colors.text.secondary};
    padding: 16px;
`;

const VoidSpace = styled.div`
    flex: 1;
`;

type CollapsibleProps = {
    title: string;
    description: string;
};

const Collapsible = ({
    title, description
}:CollapsibleProps ) => {
    const [showContent, setShowContent] = useState(false);
    const toggleAccordion = () => setShowContent(!showContent);

    return (
        <Wrapper>
            <Header onClick={toggleAccordion}>
                <HeaderTitle>
                    {title}
                </HeaderTitle>
                            <IconWrapper>
                            </IconWrapper>
            </Header>
            {showContent && (
                <>
                   <ContentRow>
                                <Content>
                                    {description}
                                </Content>
                                <VoidSpace />
                            </ContentRow>

                </>
            )}
        </Wrapper>
    );
};

export default Collapsible;
