import React, { useContext, useEffect } from 'react';

import { MedicinesContext, MedicinaContextType } from '../App';

const MedicineList = () => {
    const { medicinas, setMedicinasList} = useContext(MedicinesContext) as MedicinaContextType;
    const url = 'https://run.mocky.io/v3/1f00949f-adc2-4484-ad6d-4f565e82ad30';

    const loadMedicinaData = async () => {
        try {
          const response: any = await fetch(url);
          await response.json();

          if (response) {
            setMedicinasList(response);
          }
        } catch (err) {
          console.log(err);
        }
      };

      useEffect(() => {
        loadMedicinaData();
      }, []);

  /*useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setMedicinasList(data.data);

      });
  }, []);*/

    console.log("las medicines", medicinas);
    return (
        <section className='first-section-wrapper'>
            <h2>Lista de medicamentos</h2>

            <h3>Pastillas</h3>
            <table>
                <thead>
                    <tr>
                        <th>
                            Marca que ofrecemos
                        </th>
                        <th>
                            Laboratorio
                        </th>
                        <th>
                            Precio(sin cobertura m√©dica)
                        </th>
                    </tr>  
                </thead>
                 <tbody>
                 {(medicinas.length !== 0 && medicinas.length !== undefined) && medicinas.pills.map((pill: { brand: string, laboratory: string, price: number }) => (
                    <tr>
                        <td>{pill.brand}</td>
                        <td>{pill.laboratory}</td>
                        <td>{pill.price}</td>
                    </tr>
                 ))}
                 </tbody>

            </table>
            <h3>Anillos</h3>

            <h3>Parches</h3>
        </section>
    );
}

export default MedicineList;
