import React, { useContext, useState } from 'react';
import Collapsible from 'react-collapsible';
import { QuestionsContext } from '../context/QuestionsContext';
import Arrow from '../assets/arrow.svg';

const HeaderCollapsible = ({item, questionOpen}) => (
    <div className='header-collapsible'>
                        <p>{item.title}</p>
                        <div className={questionOpen ? 'arrow-open' : 'arrow-closed'}>
                            <img src={Arrow} alt="Ver mÃ¡s"/>
                        </div>
                    </div>
);

const FrequentQuestions = () => {
    const { questionsData } = useContext(QuestionsContext);
    const [questionOpen, setQuestionOpen] = useState();

    const handleChange = (id: any) => {
        setQuestionOpen(id);
    }

    return (
        <section className='second-section-wrapper'>
            <h2>Preguntas frecuentes</h2>
            {questionsData && (
                questionsData.data.map((item: { title: string; description: string; id: string; }) => (
                <Collapsible
                    key={item.id}
                    trigger={<HeaderCollapsible item={item} questionOpen={questionOpen}/>}
                    open={questionOpen === item.id}
                    onTriggerOpening={() => handleChange(item.id)}
                >
                    <p className='collapsible-description'>{item.description}</p>
                </Collapsible>
                ))
                )}    
        </section>
    );
}

export default FrequentQuestions;
