import React, { createContext } from 'react';
import logo from './logo.svg';
import './App.css';
import Header from './components/Header';
import CallToActionSection from './components/CallToActionSection';
import MedicineList from './components/MedicineList';
import FrequentQuestions from './components/FrequentQuestions';
import Footer from './components/Footer';
import Modal from './components/Modal';
import { useState, useEffect } from 'react';

export type MedicinesProps = {
  medicines: object;
}

export type MedicineContextProps = {
  medicines: MedicinesProps[],
}

export const MedicinesContext = React.createContext<MedicineContextProps | null>(null);


function App() {

  const url = 'https://run.mocky.io/v3/1f00949f-adc2-4484-ad6d-4f565e82ad30';
  const [medicines, setMedicinesList] = useState<MedicineContextProps[]>([]);

  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setMedicinesList(data);
      });
  }, []);

  console.log("medicines ", medicines);

  const [showModal, setShowModal] = useState(false);

  const handleChange = () => {
    showModal === false ? setShowModal(true) : setShowModal(false)
  }

  return (
    <div className="App">
      <MedicinesContext.Provider value={{ medicines }}>
      <Header />
      <CallToActionSection 
        handleChange={handleChange}
      />
      <MedicineList />
      <FrequentQuestions />
      {showModal &&
        <Modal handleChange={handleChange}/>
      }
      <Footer />
      </MedicinesContext.Provider>
    </div>
  );
}

export default App;
