function App() {
  const [showModal, setShowModal] = useState(false);

  const handleChange = () => {
    showModal === false ? setShowModal(true) : setShowModal(false)
  }

  return (
      <MedicineProvider>
        <div className="App">
          <Header />
          <CallToActionSection 
            handleChange={handleChange}
          />

          <MedicineList />
          <FrequentQuestions />
          {showModal &&
            <Modal handleChange={handleChange}/>
          }
          <Footer />
        </div>
      </MedicineProvider>
  );
}

export default App;
