import React, { useState, FunctionComponent } from 'react';
import styled from 'styled-components';

const StyledContent = styled.div`
    color: #7C7C7C;
    font-size: 16px;
    overflow: hidden;
  transition: height 0.2s ease-in-out;
`;

const HeaderTitle = styled.p`
    flex: 6;
    font-size: 15px;
    line-height: 18px;
`;

type CollapsibleProps = {
    title: string;
    description: string;
    id: string;
    questionOpen: any;
    setQuestionOpen: (questionOpen: any) => void;
};

const Collapsible = ({
    title, 
    description, 
    id,
    questionOpen,
    setQuestionOpen,
}:CollapsibleProps ) => {
    const [showContent, setShowContent] = useState(false);
    const toggleAccordion = () => {
        setQuestionOpen(id);
        console.log("questionOpen ", questionOpen);
        setShowContent(!showContent)
    };

    return (
        <div className='Collapsible'>
            <div className='header-collapsible' onClick={toggleAccordion}>
                <p className='header-collapsible-title'>
                    {title}
                </p>
            </div>
            {(showContent && questionOpen === id)  && (
                <StyledContent>
                        <p>{description}</p>
                </StyledContent>
               )}
        </div>
    );
};

export default Collapsible;
