import React from 'react';
import './App.css';
import Header from './components/Header';
import CallToActionSection from './components/CallToActionSection';
import MedicineList from './components/MedicineList';
import FrequentQuestions from './components/FrequentQuestions';
import Footer from './components/Footer';
import Modal from './components/Modal';
import { useState, useEffect } from 'react';
import { MedicinaData, ChildrenProps } from './types/types';
import {Â MedicineContext } from './context/MedicineContext';


const MedicinaProvider = ({ children }: ChildrenProps) => {
  const [medicinaData, setMedicinaData] = useState<MedicinaData | null>(null);

  const url = 'https://run.mocky.io/v3/1f00949f-adc2-4484-ad6d-4f565e82ad30';


  const loadMedicinaData = async () => {
    fetch(url)
        .then((response) => response.json())
        .then((data) => {
        setMedicinaData(data.data);
      });
  };

  const value = {
    medicinaData,
    setMedicinaData,
    loadMedicinaData,
  };

  useEffect(() => {
    loadMedicinaData();
  }, []);

  return (
    <MedicineContext.Provider value={value}>
      {children}
    </MedicineContext.Provider>
  );
};

function App() {
  const [showModal, setShowModal] = useState(false);

  const handleChange = () => {
    showModal === false ? setShowModal(true) : setShowModal(false)
  }

  return (
      <MedicinaProvider>
        <div className="App">
          <Header />
          <CallToActionSection 
            handleChange={handleChange}
          />

          <MedicineList />
          <FrequentQuestions />
          {showModal &&
            <Modal handleChange={handleChange}/>
          }
          <Footer />
        </div>
      </MedicinaProvider>
  );
}

export default App;
