import React, { createContext, ReactNode } from 'react';
import logo from './logo.svg';
import './App.css';
import Header from './components/Header';
import CallToActionSection from './components/CallToActionSection';
import MedicineList from './components/MedicineList';
import FrequentQuestions from './components/FrequentQuestions';
import Footer from './components/Footer';
import Modal from './components/Modal';
import { useState, useEffect } from 'react';

/*export type MedicinesProps = {
  medicines: any;
}*/

export type MedicineContextProps = {
  medicines: any;
}


interface Props {
  children: ReactNode,
}

export const MedicinesContext = createContext<MedicineContextProps | null>(null);

export function MedicinesContextProvider({ children }: Props) {
  const url = 'https://run.mocky.io/v3/1f00949f-adc2-4484-ad6d-4f565e82ad30';
  const [medicines, setMedicinesList] = useState<MedicineContextProps[]>([]);

  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setMedicinesList(data.data);
      });
  }, []);

  return <MedicinesContext.Provider value={{medicines}}>{children}</MedicinesContext.Provider>
}

function App() {

  /*const url = 'https://run.mocky.io/v3/1f00949f-adc2-4484-ad6d-4f565e82ad30';
  const [medicines, setMedicinesList] = useState<MedicineContextProps[]>([]);

  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setMedicinesList(data.data);
      });
  }, []);*/


  const [showModal, setShowModal] = useState(false);

  const handleChange = () => {
    showModal === false ? setShowModal(true) : setShowModal(false)
  }

  return (
      <MedicinesContextProvider>
        <div className="App">

          <Header />
          <CallToActionSection 
            handleChange={handleChange}
          />
          <MedicineList />
          {/*(medicines.length !== 0 && medicines.length !== undefined) ? (
              <MedicineList />
          ) : "Cargando"*/}
          <FrequentQuestions />
          {showModal &&
            <Modal handleChange={handleChange}/>
          }
          <Footer />
      </div>

      </MedicinesContextProvider>
  );
}

export default App;
