import React, { useState, FunctionComponent } from 'react';
import styled from 'styled-components';

const ContentRow = styled.div`
    flex: 1;
    flex-direction: row;
`;

const Header = styled.div`
    flex: 1;
    flex-direction: row;
`;

const HeaderTitle = styled.p`
    flex: 6;
    font-size: 15px;
    line-height: 18px;
`;

const IconWrapper = styled.div`
    flex: 1;
    align-items: flex-end;
`;

const CustomIconWrapper = styled.div`
    flex: 1;
    align-items: flex-end;
    padding: 12px;
`;

const Content = styled.p`
    flex: 6;
    margin-top: 12px;
    font-size: 13px;
    line-height: 18px;
`;

const BodyWrapper = styled.div`
    flex: 1;
`;

const CustomHeaderTitle = styled.p`
    font-size: ${({ theme }) => theme.fonts.size[14]};
    color: ${({ theme }) => theme.colors.text.secondary};
    padding: 16px;
`;

const VoidSpace = styled.div`
    flex: 1;
`;

type CollapsibleProps = {
    title: string;
    description: string;
    id: string;
    questionOpen: any;
    setQuestionOpen: (questionOpen: any) => void;
};

const Collapsible = ({
    title, 
    description, 
    id,
    questionOpen,
    setQuestionOpen,
}:CollapsibleProps ) => {
    const [showContent, setShowContent] = useState(false);
    const toggleAccordion = () => {
        setQuestionOpen(id);
        console.log("questionOpen ", questionOpen);
        setShowContent(!showContent)
    };

    return (
        <div>
            <Header onClick={toggleAccordion}>
                <HeaderTitle>
                    {title}
                </HeaderTitle>
            </Header>
            {(showContent && questionOpen === id)  && (
                <>
                   <div>
                        <Content>
                            {description}
                        </Content>
                    </div>

                </>
            )}
        </div>
    );
};

export default Collapsible;
