import React, { createContext, ReactNode } from 'react';
import logo from './logo.svg';
import './App.css';
import Header from './components/Header';
import CallToActionSection from './components/CallToActionSection';
import MedicineList from './components/MedicineList';
import FrequentQuestions from './components/FrequentQuestions';
import Footer from './components/Footer';
import Modal from './components/Modal';
import { useState, useEffect } from 'react';

export type MedicineContextProps = {
  pills: any;
  medicines: any;
}

export type MedicinaContextType= {
  medicinas: MedicineContextProps,
  setMedicinasList: (value: MedicineContextProps) => void
}

interface Props {
  children: ReactNode;
}

export const MedicinesContext = createContext({});

export function MedicinasProvider({ children }: Props) {
  const [medicinas, setMedicinasList] = useState<MedicineContextProps[]>([]);
  return <MedicinesContext.Provider value={{medicinas, setMedicinasList}}>{children}</MedicinesContext.Provider>
}

function App() {

  /*const url = 'https://run.mocky.io/v3/1f00949f-adc2-4484-ad6d-4f565e82ad30';
  const [medicines, setMedicinesList] = useState<MedicineContextProps[]>([]);

  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setMedicinesList(data.data);
        console.log("medicines ", medicines);

      });
  }, []);*/


  const [showModal, setShowModal] = useState(false);

  const handleChange = () => {
    showModal === false ? setShowModal(true) : setShowModal(false)
  }

  return (
      <MedicinasProvider>
        <div className="App">
          <Header />
          <CallToActionSection 
            handleChange={handleChange}
          />

          <MedicineList />
          <FrequentQuestions />
          {showModal &&
            <Modal handleChange={handleChange}/>
          }
          <Footer />
        </div>
      </MedicinasProvider>
  );
}

export default App;
